package exam;

public class Example11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * 논리부정연산자
		 * boolean 값을 가지는 피연산자 앞에 
		 * !를 붙혀서 값을 반대로 바꾸는 역활
		 * true-false
		 * false-true
		 * 
		 * */
		boolean a = true;
		boolean b = false;
		System.out.println(!a);
		System.out.println(!b);
		
	}

}
