package exam;

public class Example14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * 관계연산자
		 * <,<=,>,>=,==,!=
		 * 2개의 피연산자를 부호로 비교하여 그 관계가 
		 * true 인지 false 인지 판별하는 연산자
		 */
		int a = 10;
		int b = 20;
		System.out.println(a>b);
		System.out.println(a<=b);
		System.out.println(a==b);
		System.out.println(a!=b);
		
		char ch1 = 'A';
		int num1 = 65;
		System.out.println(ch1==num1);  
		
	
	}

}
