package Exam;

public class Example12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 4;
		if(num<5) {
			System.out.println("좋아하는 숫자가 5보다 작습니다.");
		}else if(num>5) {
			System.out.println("좋아하는 숫자가 5보다 큽니다.");
		}else {
			System.out.println("좋아하는 숫자가 5입니다.");
		}

	}

}
